import React from 'react';
function DarkBox(props){
    // alert(props.imdbID)


  return(
    <div id="id_dark" className="dark container-fluid center">
        <div className="dark_inside text-center">
            {(imd)? <div><img src={imd.Poster} />
            <h2>{imd.Title}</h2>
            <h2>{imd.Year}</h2>
            <p>{imd.Plot}</p>

            </div>:""}
            
             {/* <button className="btn btn-danger sm-3" onClick={closeDark}>Close</button> */}
            <button className="btn btn-danger sm-3" onClick={props.showDarkbox}>Close</button>
        </div>
    </div>
  )
}

export default DarkBox
