import React, { useRef, useState } from 'react';
import OMDbApi from './omdbApi';
function Search(props) {
    let searchInput = useRef()
    let [inputLocation, setInputLocation] = useState("css_searchBoxCenter")


    const onInput = () => {
        if (searchInput.current.value === "") {
            setInputLocation("css_searchBoxCenter")
        }
        else {
            setInputLocation("css_searchBoxCenter")
        }
        props.setUserInput(searchInput.current.value);
    }

    return (
        <div className="container  searchBoxCenter" id="id_search">
            <div></div>
            <input ref={searchInput} onInput={onInput} type="text" placeholder="search ..." className="form-control w-75 searchBox_input" id="id_input" /><i className="fa fa-search"></i>
        </div>


        //         <div className="input-group col-md-4  container d-flex justify-content-center align-items-center searchBox">
