import React, { useRef, useEffect } from 'react';
import { Link, NavLink } from "react-router-dom"
import "../css_comps/atlas.css"



function Nav(props) {
    const searchInput = useRef();

    useEffect(()=>{
        searchBtn();

    },[props.name])
    const searchBtn = ()=>{
        if(searchInput.current.value ===""){
            alert("Please a Country name Courtry in the search field")
        }
        props.setName(searchInput.current.value);
        searchInput.current.value ="";
    }
    return (
        <nav className="container bg-secondary text-white overflow-hidden d-flex align-items-center justify-content-between">
            <div className="row">
                <NavLink onClick={() => { props.setName("usa") }} to={"/USA"} className="text-decoration-none">USA</NavLink>
                <NavLink onClick={() => { props.setName("israel") }} to={"/israel"} className="text-decoration-none">ISRAEL</NavLink>
                <NavLink onClick={() => { props.setName("uk") }} to={"/UK"} className="text-decoration-none">UK</NavLink>
                <NavLink onClick={() => { props.setName("france") }} to={"/France"} className="text-decoration-none">FRANCE</NavLink>
                <NavLink onClick={() => { props.setName("thailand") }} to={"/Thailand"} className="text-decoration-none">THAILAND</NavLink>

                
            </div>
                <div className="float-right">
                    <div className="row ">
                        <input ref={searchInput} type="text" placeholder="Search..." className="col-lg-8 form-control" />
                        <Link to={"/"+props.name} onClick={searchBtn} className="btn btn-success h-25">Search</Link>
                    </div>
                </div>
        </nav>

    )
}

export default Nav
