import React, {useState, useEffect} from 'react';
import { doApiGet, apiUrl } from '../services/apiService';
import CountryItem from './CountryItem';


function Main(props) {
    let [state_arr,setStateArr]=useState([]);
    let [count,setCount]=useState(1)

    useEffect(()=>{
        // let url ="https://restcountries.eu/rest/v2/name/"+props.name;
        let url ="https://restcountries.eu/rest/v2/name/"+props.name+"?fullText=true"
        doApiGet(url)
        // doApiGet(apiUrl)
        .then(data=>{
            // console.log(data);
            setCount(count+1)
            setStateArr(data)
        })

    },[props.name])
    
    return (
        <div className="container mt-3">
           {/* {state_arr.map((item,i) => {
               
                return (
                    
                    <CountryItem key ={item.callingCodes[0]} item={item} />
                )
            })} */}

         {count>0&&<CountryItem state_arr={state_arr}/>}
           
        </div>
    )
}

export default Main
