import React, {useState, useEffect} from 'react';
import { doApiGet, apiUrl } from '../services/apiService';
import CountryItem from './CountryItem';


function Main(props) {
    let [state_arr,setStateArr]=useState([]);
    let [counter,setCouter]=useState(0)

    useEffect(()=>{
        // let url ="https://restcountries.eu/rest/v2/name/"+props.name;
        let url ="https://restcountries.eu/rest/v2/name/"+props.name+"?fullText=true"
        doApiGet(url)
        // doApiGet(apiUrl)
        .then(data=>{
            // changeCounter();
            // console.log(data);
            setStateArr(data);
        })

    },[counter])
    const changeCounter =()=>{
        setCouter(counter+1)

    }
    
    return (
        <div className="container">
            <CountryItem state_arr={state_arr}/>
           
        </div>
    )
}

export default Main
