import React from 'react';
// import { doApiPost } from '../services/apiService';
// import "./up.css"
import JsonTalk from './jsonTalk';
import axios from "axios";

function UpApp(props) {

  const onSub = (e) => {
    e.preventDefault()
    console.log(e.target.id_file.files[0])

    const data1 = new FormData();
    data1.append("f", e.target.id_file.files[0]);
    console.log(data1);

    // לנסות למצוא ברחבי הרשת איך מעלים
    // קובץ דרך ריאקט בטופס ששולחים
    // 13:05 להיות כאן

    let url = "http://localhost:3000/upload/addFile"
    axios.post(url, data1)
      .then(resp => {
        console.log(resp.data);
      })
  }

  return (
    <div className="container">
      <JsonTalk />
      <nav className="d-md-flex justify-content-between my-3">
        <div className="col-md-4 d-flex p-0">
          <input type="text" className="form-control " />
          <button className="btn btn-info">seach</button>
        </div>
        <select className="form-control col-md-3" >
          <option>Home</option>
          <option>Work</option>
        </select>
      </nav>
      <form encType="multipart/form-data" onSubmit={onSub}>
        <input type="file" id="id_file" />
        <button>upload </button>
      </form>
    </div>
  )
}

export default UpApp
