import React from 'react';
import {useDispatch} from "react-redux";

 //TODO ADD new prod to cart
function ProdItem(props) {
  let dispatch = useDispatch()

  const addToCart = () => {
    let prodItem = {
      name:props.item.name,
      price:props.item.price,
      amount:1,
      id:props.item._id
    }
    // מוסיף מוצר לעגלה הגלובלית
    dispatch({type:"addToCart",prodItem})
  }

  return (
    <div className="col-lg-6 border p-2">
      <h2>{props.item.name}</h2>
      <div>Price Nis: {props.item.price}</div>
      <div>Price USD: {props.item.price_usd.toFixed(2)}</div>
      <button onClick={addToCart} className="btn btn-info">add to cart</button>
    </div>
  )
}

export default ProdItem
