import React, { useState, useEffect } from 'react';
import MoviesItem from './moviesItem';
function OMDbApi(props){
    let [movies_arr,setMoviesArr] =useState([]);

    let url ="http://www.omdbapi.com/?s=home&apikey=5a292f28";
    useEffect(()=>{
        fetch(url)
        .then(resp=>resp.json())
        .then(data=>{
            console.log(data.Search);
            setMoviesArr(data.Search)
        })
    },[])

  return(
     
    <div className="container">
         <div className="row mt-3 item ">
         { (movies_arr.length)? movies_arr.map(item=>{
            return(
            // <img key={item.imdbID} src={item.Poster}/>
            <MoviesItem key={item.imdbID} item={item}/>
            )

        }):"Not found"
        

        } 
              
            </div>
        
     
    </div> 
  )
}

export default OMDbApi
