import React, { useState, useEffect } from 'react';
import "./css/cart.css"
import CartItem from './cartItem';
import { useSelector } from "react-redux"

function Cart(props) {
  let cart_ar = useSelector(state => state.cart_ar)
  let [showCart, setShowCart] = useState(false)
  let [yPx,setY] = useState("-800px")

  // 13:05


  useEffect(() => {
    console.log("work");
    setY("-800px")
    if(showCart){
      setY("30px")
    } 
  },[showCart])

  return (
    <div className="cart_cont">
      <div className="cart_btn text-right">
        <button onClick={() => {
          setShowCart(!showCart)
        }}>show/hide cart</button>
      </div>
     
      <div style={{top:yPx,transition:"0.8s"}} className="cart" id="style-11">
        <div>
          {cart_ar.map(item => {
            return (
              <CartItem key={item.id} item={item} />

            )
          })}



        </div>

      </div>
    </div>
  )
}

export default Cart
