import React, { useEffect } from 'react';
import {useSelector,useDispatch} from "react-redux"
import { doApiGet } from '../services/apiService';
import ProdItem from './prodItem';

function MainShop(props){

  // אתם אוהבים לקרוא לסטייט מייסטור
  const counterGlobal = useSelector(state => state.counter)
  const prods_ar = useSelector(state => state.prods_ar)
  const dispatch = useDispatch()

  useEffect(() => {
    let url = "https://marketcor.herokuapp.com/prods"
    doApiGet(url)
    .then(data => {
      console.log(data);
      data.forEach(item => {
        item.price_usd = item.price / 3.4
      })
       dispatch({type:"addProd",newProds:data});
    })
  },[])


  //https://marketcor.herokuapp.com/prods

  return(
    <div>
      <h1>Main shop {counterGlobal}</h1>
      <div className="row">
        {prods_ar.map(item => {
          return(
            <ProdItem key={item._id} item={item}/>
          )
        })}
      </div>
    </div> 
  )
}

export default MainShop
